import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import NewPostForm from "./NewPostForm";
import Post from "./Post";

function BlogList() {
  const dispatch = useDispatch();
  const posts = useSelector((st) => st.posts);

  function handleCreate(post) {
    dispatch({
      type: "ADD_POST",
      post,
    });
  }

  function handleUpdate(id, updatedPost) {
    dispatch({
      type: "UPDATE_POST",
      id,
      updatedPost,
    });
  }

  function handleDelete(id) {
    dispatch({ type: "REMOVE_POST", id });
  }
  const postCards = Object.keys(posts).map((id) => (
    <div className="col-md-3 mb-3" key={id}>
      <div className="card">
        <div className="card-body">
          <h2 className="card-title text-center">
            <Link to={`/${id}`}>{posts[id].title}</Link>
          </h2>
          <Post
            deletePost={handleDelete}
            id={post.id}
            key={post.id}
            post = {post}
            updatePost={handleUpdate}
          />
        </div>
      </div>
    </div>
  ));

  return (
    <div>
      <h4>
        Welcome to <b>Microblog</b>, our innovative site for communicating on
        the information superhighway.
      </h4>
      <div className="row">{postCards}</div>
      <NewPostForm createPost = {handleCreate}/>
    </div>
  );
}

export default BlogList;
