import React, { useState, useHistory } from "react";

function Post({ body, description, title, updatePost, deletePost, id }) {
  const [editingPost, setEditingPost] = useState(id);
  const [isEditing, setIsEditing] = useState(false);

  const history = useHistory();

  function handleRemove() {
    deletePost(id);
    history.push("/");
  }

  function toggleEdit() {
    setIsEditing((e) => !e);
  }

  function handleChange(evt) {
    setEditingPost(evt.target.value);
  }

  function stopEditing(evt) {
    evt.preventDefault();
    updatePost(id, title, description, body);
    setIsEditing(false);
    history.push("/");
  }

  if (isEditing) {
    return (
      <div>
        <form onSubmit={stopEditing}>
          <input type="text" value={editingPost} onChange={handleChange} />
          <button>Stop Editing</button>
        </form>
      </div>
    );
  }

  return (
    <div>
      <button onClick={toggleEdit}>Edit</button>
      <button onClick={handleRemove}>X</button>
      <li>{editingPost}</li>
    </div>
  );
}

export default Post;
