import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { uuid } from 'uuidv4';

const DEFAULT_FORM = {
  title: "",
  description: "",
  body: "",
};
function NewPostForm({ createPost }) {
  const [form, setForm] = useState(DEFAULT_FORM);
  const history = useHistory();
  function handleChange(e) {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    createPost({ ...form, id: uuid() });
    setForm(DEFAULT_FORM);
    history.push("/");
  }

  return (
    <div>
      <h2>New Post</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title</label>
        <input
          name="title"
          type="text"
          onChange={handleChange}
          value={form.title}
        />
        <label htmlFor="title">Description:</label>
        <input
          name="description"
          type="text"
          onChange={handleChange}
          value={form.description}
        />
        <label htmlFor="body">Body:</label>
        <input
          name="body"
          type="text"
          rows={10}
          onChange={handleChange}
          value={form.body}
        />
        <button type="submit">Save</button>
        <Link to="/">
          <button>Cancel</button>
        </Link>
      </form>
    </div>
  );
}

export default NewPostForm;


<form className="mb-4" onSubmit={handleSubmit}>

<div className="form-group">
<label htmlFor="editform-title">Title:</label>
<input onChange={handleChange}
        id="editform-title"
        name="title"
        className="form-control"
        value={postData.title} />
</div>

<div className="form-group">
<label htmlFor="editform-description">Description:</label>
<input onChange={handleChange}
        id="editform-description"
        name="description"
        className="form-control"
        value={postData.description} />
</div>

<div className="form-group">
<label htmlFor="editform-body">Body:</label>
<textarea onChange={handleChange}
          id="editform-body"
          name="body"
          className="form-control"
          rows={10}
          value={postData.body} />
</div>

<button className="btn btn-primary mr-2">Save</button>
<button onClick={cancel} className="btn btn-secondary">Cancel</button>
</form>
